import { createStore } from "vuex";

export default createStore({
  // for the global store data;
  state: {
    firstname: "Bruce",
    lastname: "wayne",
    favs: [],
  },
  mutations: {},
  actions: {
    addtofav() {},
  },
  getters: {
    fullname: function (state) {
      return `${state.firstname}, ${state.lastname}`;
    },
  },
});
